INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7310, 'Giant Frog', 2, 'Bite', 1, '1d6', 3, 'Melee Weapon Attack: +3 to hit, reach 5 ft., one target. Hit: 4 (1d6 + 1) piercing damage, and the target is grappled (escape DC 11). Until this grapple ends, the target is restrained, and the frog can''t bite another target.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7311, 'Giant Frog', 2, 'Swallow', 0, NULL, 0, 'The frog makes one bite attack against a Small or smaller target it is grappling. If the attack hits, the target is swallowed, and the grapple ends. The swallowed target is blinded and restrained, it has total cover against attacks and other effects outside the frog, and it takes 5 (2d4) acid damage at the start of each of the frog''s turns. The frog can have only one target swallowed at a time. If the frog dies, a swallowed creature is no longer restrained by it and can escape from the corpse using 5 ft. of movement, exiting prone.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7333, 'Giant Scorpion', 2, 'Claw', 2, '1d8', 4, 'Melee Weapon Attack: +4 to hit, reach 5 ft., one target. Hit: 6 (1d8 + 2) bludgeoning damage, and the target is grappled (escape DC 12). The scorpion has two claws, each of which can grapple only one target.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7334, 'Giant Scorpion', 2, 'Multiattack', 0, NULL, 0, 'The scorpion makes three attacks: two with its claws and one with its sting.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7335, 'Giant Scorpion', 2, 'Sting', 2, '1d10', 4, 'Melee Weapon Attack: +4 to hit, reach 5 ft., one creature. Hit: 7 (1d10 + 2) piercing damage, and the target must make a DC 12 Constitution saving throw, taking 22 (4d10) poison damage on a failed save, or half as much damage on a successful one.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7363, 'Gibbering Mouther', 1, 'Aberrant Ground', 0, NULL, 0, 'The ground in a 10-foot radius around the mouther is doughlike difficult terrain. Each creature that starts its turn in that area must succeed on a DC 10 Strength saving throw or have its speed reduced to 0 until the start of its next turn.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7364, 'Gibbering Mouther', 1, 'Gibbering', 0, NULL, 0, 'The mouther babbles incoherently while it can see any creature and isn''t incapacitated. Each creature that starts its turn within 20 feet of the mouther and can hear the gibbering must succeed on a DC 10 Wisdom saving throw. On a failure, the creature can''t take reactions until the start of its next turn and rolls a d8 to determine what it does during its turn. On a 1 to 4, the creature does nothing. On a 5 or 6, the creature takes no action or bonus action and uses all its movement to move in a randomly determined direction. On a 7 or 8, the creature makes a melee attack against a randomly determined creature within its reach or does nothing if it can''t make such an attack.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7365, 'Gibbering Mouther', 2, 'Multiattack', 0, NULL, 0, 'The gibbering mouther makes one bite attack and, if it can, uses its Blinding Spittle.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7366, 'Gibbering Mouther', 2, 'Bites', 0, '5d6', 2, 'Melee Weapon Attack: +2 to hit, reach 5 ft., one creature. Hit: 17 (5d6) piercing damage. If the target is Medium or smaller, it must succeed on a DC 10 Strength saving throw or be knocked prone. If the target is killed by this damage, it is absorbed into the mouther.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7367, 'Gibbering Mouther', 2, 'Blinding Spittle (Recharge 5-6)', 0, NULL, 0, 'The mouther spits a chemical glob at a point it can see within 15 feet of it. The glob explodes in a blinding flash of light on impact. Each creature within 5 feet of the flash must succeed on a DC 13 Dexterity saving throw or be blinded until the end of the mouther''s next turn.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7400, 'Green Dragon Wyrmling', 1, 'Amphibious', 0, NULL, 0, 'The dragon can breathe air and water.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7401, 'Green Dragon Wyrmling', 2, 'Bite', 3, '1d10 + 1d6', 4, 'Melee Weapon Attack: +4 to hit, reach 5 ft., one target. Hit: 7 (1d10 + 2) piercing damage plus 3 (1d6) poison damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7402, 'Green Dragon Wyrmling', 2, 'Poison Breath (Recharge 5-6)', 0, '6d6', 0, 'The dragon exhales poisonous gas in a 15-foot cone. Each creature in that area must make a DC 11 Constitution saving throw, taking 21 (6d6) poison damage on a failed save, or half as much damage on a successful one.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7429, 'Gynosphinx', 1, 'Inscrutable', 0, NULL, 0, 'The sphinx is immune to any effect that would sense its emotions or read its thoughts, as well as any divination spell that it refuses. Wisdom (Insight) checks made to ascertain the sphinx''s intentions or sincerity have disadvantage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7430, 'Gynosphinx', 1, 'Magic Weapons', 0, NULL, 0, 'The sphinx''s weapon attacks are magical.');
