INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7435, 'Gynosphinx', 3, 'Teleport (Costs 2 Actions)', 0, NULL, 0, 'The sphinx magically teleports, along with any equipment it is wearing or carrying, up to 120 feet to an unoccupied space it can see.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7436, 'Gynosphinx', 3, 'Cast a Spell (Costs 3 Actions)', 0, NULL, 0, 'The sphinx casts a spell from its list of prepared spells, using a spell slot as normal.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7453, 'Hezrou', 1, 'Stench', 0, NULL, 0, 'Any creature that starts its turn within 10 feet of the hezrou must succeed on a DC 14 Constitution saving throw or be poisoned until the start of its next turn. On a successful saving throw, the creature is immune to the hezrou''s stench for 24 hours.');
