INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7216, 'Erinyes', 2, 'Longbow', 3, '1d8 + 3d8', 7, 'Ranged Weapon Attack: +7 to hit, range 150/600 ft., one target. Hit: 7 (1d8 + 3) piercing damage plus 13 (3d8) poison damage, and the target must succeed on a DC 14 Constitution saving throw or be poisoned. The poison lasts until it is removed by the lesser restoration spell or similar magic.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7217, 'Erinyes', 2, 'Variant: Rope of Entanglement', 0, NULL, 0, 'Some erinyes carry a rope of entanglement (detailed in the Dungeon Master''s Guide). When such an erinyes uses its Multiattack, the erinyes can use the rope in place of two of the attacks.');
