INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7218, 'Ettercap', 1, 'Spider Climb', 0, NULL, 0, 'The ettercap can climb difficult surfaces, including upside down on ceilings, without needing to make an ability check.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7219, 'Ettercap', 1, 'Web Sense', 0, NULL, 0, 'While in contact with a web, the ettercap knows the exact location of any other creature in contact with the same web.');
