INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7224, 'Ettercap', 2, 'Web (Recharge 5-6)', 0, NULL, 0, 'Ranged Weapon Attack: +4 to hit, range 30/60 ft., one Large or smaller creature. Hit: The creature is restrained by webbing. As an action, the restrained creature can make a DC 11 Strength check, escaping from the webbing on a success. The effect ends if the webbing is destroyed. The webbing has AC 10, 5 hit points, is vulnerable to fire damage and immune to bludgeoning damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7225, 'Ettercap', 2, 'Variant: Web Garrote', 2, '1d4', 4, 'Melee Weapon Attack: +4 to hit, reach 5 ft., one Medium or Small creature against which the ettercap has advantage on the attack roll. Hit: 4 (1d4 + 2) bludgeoning damage, and the target is grappled (escape DC 12). Until this grapple ends, the target can''t breathe, and the ettercap has advantage on attack rolls against it.');
