INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7057, 'Chain Devil', 1, 'Devil''s Sight', 0, NULL, 0, 'Magical darkness doesn''t impede the devil''s darkvision.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7058, 'Chain Devil', 1, 'Magic Resistance', 0, NULL, 0, 'The devil has advantage on saving throws against spells and other magical effects.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7059, 'Chain Devil', 2, 'Multiattack', 0, NULL, 0, 'The devil makes two attacks with its chains.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7060, 'Chain Devil', 2, 'Chain', 4, '2d6', 8, 'Melee Weapon Attack: +8 to hit, reach 10 ft., one target. Hit: 11 (2d6 + 4) slashing damage. The target is grappled (escape DC 14) if the devil isn''t already grappling a creature. Until this grapple ends, the target is restrained and takes 7 (2d6) piercing damage at the start of each of its turns.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7061, 'Chain Devil', 2, 'Animate Chains (Recharges after a Short or Long Rest)', 0, NULL, 0, 'Up to four chains the devil can see within 60 feet of it magically sprout razor-edged barbs and animate under the devil''s control, provided that the chains aren''t being worn or carried.
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7062, 'Chimera', 2, 'Multiattack', 0, NULL, 0, 'The chimera makes three attacks: one with its bite, one with its horns, and one with its claws. When its fire breath is available, it can use the breath in place of its bite or horns.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7063, 'Chimera', 2, 'Bite', 4, '2d6', 7, 'Melee Weapon Attack: +7 to hit, reach 5 ft., one target. Hit: 11 (2d6 + 4) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7064, 'Chimera', 2, 'Horns', 4, '1d12', 7, 'Melee Weapon Attack: +7 to hit, reach 5 ft., one target. Hit: 10 (1d12 + 4) bludgeoning damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7065, 'Chimera', 2, 'Claws', 4, '2d6', 7, 'Melee Weapon Attack: +7 to hit, reach 5 ft., one target. Hit: 11 (2d6 + 4) slashing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7066, 'Chimera', 2, 'Fire Breath (Recharge 5-6)', 0, '7d8', 0, 'The dragon head exhales fire in a 15-foot cone. Each creature in that area must make a DC 15 Dexterity saving throw, taking 31 (7d8) fire damage on a failed save, or half as much damage on a successful one.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7067, 'Chuul', 1, 'Amphibious', 0, NULL, 0, 'The chuul can breathe air and water.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7068, 'Chuul', 1, 'Sense Magic', 0, NULL, 0, 'The chuul senses magic within 120 feet of it at will. This trait otherwise works like the detect magic spell but isn''t itself magical.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7069, 'Chuul', 2, 'Multiattack', 0, NULL, 0, 'The chuul makes two pincer attacks. If the chuul is grappling a creature, the chuul can also use its tentacles once.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7070, 'Chuul', 2, 'Pincer', 4, '2d6', 6, 'Melee Weapon Attack: +6 to hit, reach 10 ft., one target. Hit: 11 (2d6 + 4) bludgeoning damage. The target is grappled (escape DC 14) if it is a Large or smaller creature and the chuul doesn''t have two other creatures grappled.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7071, 'Chuul', 2, 'Tentacles', 0, NULL, 0, 'One creature grappled by the chuul must succeed on a DC 13 Constitution saving throw or be poisoned for 1 minute. Until this poison ends, the target is paralyzed. The target can repeat the saving throw at the end of each of its turns, ending the effect on itself on a success.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7072, 'Clay Golem', 1, 'Acid Absorption', 0, NULL, 0, 'Whenever the golem is subjected to acid damage, it takes no damage and instead regains a number of hit points equal to the acid damage dealt.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7073, 'Clay Golem', 1, 'Berserk', 0, NULL, 0, 'Whenever the golem starts its turn with 60 hit points or fewer, roll a d6. On a 6, the golem goes berserk. On each of its turns while berserk, the golem attacks the nearest creature it can see. If no creature is near enough to move to and attack, the golem attacks an object, with preference for an object smaller than itself. Once the golem goes berserk, it continues to do so until it is destroyed or regains all its hit points.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7074, 'Clay Golem', 1, 'Immutable Form', 0, NULL, 0, 'The golem is immune to any spell or effect that would alter its form.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7075, 'Clay Golem', 1, 'Magic Resistance', 0, NULL, 0, 'The golem has advantage on saving throws against spells and other magical effects.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7076, 'Clay Golem', 1, 'Magic Weapons', 0, NULL, 0, 'The golem''s weapon attacks are magical.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7077, 'Clay Golem', 2, 'Multiattack', 0, NULL, 0, 'The golem makes two slam attacks.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7078, 'Clay Golem', 2, 'Slam', 5, '2d10', 8, 'Melee Weapon Attack: +8 to hit, reach 5 ft., one target. Hit: 16 (2d10 + 5) bludgeoning damage. If the target is a creature, it must succeed on a DC 15 Constitution saving throw or have its hit point maximum reduced by an amount equal to the damage taken. The target dies if this attack reduces its hit point maximum to 0. The reduction lasts until removed by the greater restoration spell or other magic.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7079, 'Clay Golem', 2, 'Haste (Recharge 5-6)', 0, NULL, 0, 'Until the end of its next turn, the golem magically gains a +2 bonus to its AC, has advantage on Dexterity saving throws, and can use its slam attack as a bonus action.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7080, 'Cloaker', 1, 'Damage Transfer', 0, NULL, 0, 'While attached to a creature, the cloaker takes only half the damage dealt to it (rounded down). and that creature takes the other half.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7081, 'Cloaker', 1, 'False Appearance', 0, NULL, 0, 'While the cloaker remains motionless without its underside exposed, it is indistinguishable from a dark leather cloak.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7082, 'Cloaker', 1, 'Light Sensitivity', 0, NULL, 0, 'While in bright light, the cloaker has disadvantage on attack rolls and Wisdom (Perception) checks that rely on sight.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7083, 'Cloaker', 2, 'Multiattack', 0, NULL, 0, 'The cloaker makes two attacks: one with its bite and one with its tail.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7084, 'Cloaker', 2, 'Bite', 3, '2d6', 6, 'Melee Weapon Attack: +6 to hit, reach 5 ft., one creature. Hit: 10 (2d6 + 3) piercing damage, and if the target is Large or smaller, the cloaker attaches to it. If the cloaker has advantage against the target, the cloaker attaches to the target''s head, and the target is blinded and unable to breathe while the cloaker is attached. While attached, the cloaker can make this attack only against the target and has advantage on the attack roll. The cloaker can detach itself by spending 5 feet of its movement. A creature, including the target, can take its action to detach the cloaker by succeeding on a DC 16 Strength check.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7085, 'Cloaker', 2, 'Tail', 3, '1d8', 6, 'Melee Weapon Attack: +6 to hit, reach 10 ft., one creature. Hit: 7 (1d8 + 3) slashing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7822, 'Sea Hag', 1, 'Amphibious', 0, NULL, 0, 'The hag can breathe air and water.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7086, 'Cloaker', 2, 'Moan', 0, NULL, 0, 'Each creature within 60 feet of the cloaker that can hear its moan and that isn''t an aberration must succeed on a DC 13 Wisdom saving throw or become frightened until the end of the cloaker''s next turn. If a creature''s saving throw is successful, the creature is immune to the cloaker''s moan for the next 24 hours.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7087, 'Cloaker', 2, 'Phantasms (Recharges after a Short or Long Rest)', 0, NULL, 0, 'The cloaker magically creates three illusory duplicates of itself if it isn''t in bright light. The duplicates move with it and mimic its actions, shifting position so as to make it impossible to track which cloaker is the real one. If the cloaker is ever in an area of bright light, the duplicates disappear.
