INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7116, 'Darkmantle', 1, 'False Appearance', 0, NULL, 0, 'While the darkmantle remains motionless, it is indistinguishable from a cave formation such as a stalactite or stalagmite.');
