INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7133, 'Deva', 2, 'Healing Touch (3/Day)', 0, NULL, 0, 'The deva touches another creature. The target magically regains 20 (4d8 + 2) hit points and is freed from any curse, disease, poison, blindness, or deafness.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7134, 'Deva', 2, 'Change Shape', 0, NULL, 0, 'The deva magically polymorphs into a humanoid or beast that has a challenge rating equal to or less than its own, or back into its true form. It reverts to its true form if it dies. Any equipment it is wearing or carrying is absorbed or borne by the new form (the deva''s choice).
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7135, 'Dire Wolf', 1, 'Keen Hearing and Smell', 0, NULL, 0, 'The wolf has advantage on Wisdom (Perception) checks that rely on hearing or smell.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7136, 'Dire Wolf', 1, 'Pack Tactics', 0, NULL, 0, 'The wolf has advantage on an attack roll against a creature if at least one of the wolf''s allies is within 5 ft. of the creature and the ally isn''t incapacitated.');
