INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7192, 'Dust Mephit', 2, 'Blinding Breath (Recharge 6)', 0, NULL, 0, 'The mephit exhales a 15-foot cone of blinding dust. Each creature in that area must succeed on a DC 10 Dexterity saving throw or be blinded for 1 minute. A creature can repeat the saving throw at the end of each of its turns, ending the effect on itself on a success.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7193, 'Dust Mephit', 2, 'Variant: Summon Mephits (1/Day)', 0, NULL, 0, 'The mephit has a 25 percent chance of summoning 1d4 mephits of its kind. A summoned mephit appears in an unoccupied space within 60 feet of its summoner, acts as an ally of its summoner, and can''t summon other mephits. It remains for 1 minute, until it or its summoner dies, or until its summoner dismisses it as an action.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7194, 'Eagle', 1, 'Keen Sight', 0, NULL, 0, 'The eagle has advantage on Wisdom (Perception) checks that rely on sight.');
