INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7205, 'Efreeti', 2, 'Hurl Flame', 0, '5d6', 7, 'Ranged Spell Attack: +7 to hit, range 120 ft., one target. Hit: 17 (5d6) fire damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7206, 'Elephant', 1, 'Trampling Charge', 0, NULL, 0, 'If the elephant moves at least 20 ft. straight toward a creature and then hits it with a gore attack on the same turn, that target must succeed on a DC 12 Strength saving throw or be knocked prone. If the target is prone, the elephant can make one stomp attack against it as a bonus action.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7207, 'Elephant', 2, 'Gore', 6, '3d8', 8, 'Melee Weapon Attack: +8 to hit, reach 5 ft., one target. Hit: 19 (3d8 + 6) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7208, 'Elephant', 2, 'Stomp', 6, '3d10', 8, 'Melee Weapon Attack: +8 to hit, reach 5 ft., one prone creature. Hit: 22 (3d10 + 6) bludgeoning damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7249, 'Flying Sword', 1, 'Antimagic Susceptibility', 0, NULL, 0, 'The sword is incapacitated while in the area of an antimagic field. If targeted by dispel magic, the sword must succeed on a Constitution saving throw against the caster''s spell save DC or fall unconscious for 1 minute.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7250, 'Flying Sword', 1, 'False Appearance', 0, NULL, 0, 'While the sword remains motionless and isn''t flying, it is indistinguishable from a normal sword.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7251, 'Flying Sword', 2, 'Longsword', 1, '1d8', 3, 'Melee Weapon Attack: +3 to hit, reach 5 ft., one target. Hit: 5 (1d8 + 1) slashing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7282, 'Giant Badger', 2, 'Bite', 1, '1d6', 3, 'Melee Weapon Attack: +3 to hit, reach 5 ft., one target. Hit: 4 (1d6 + 1) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7283, 'Giant Badger', 2, 'Claws', 1, '2d4', 3, 'Melee Weapon Attack: +3 to hit, reach 5 ft., one target. Hit: 6 (2d4 + 1) slashing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7306, 'Giant Fire Beetle', 1, 'Illumination', 0, NULL, 0, 'The beetle sheds bright light in a 10-foot radius and dim light for an additional 10 ft..');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7307, 'Giant Fire Beetle', 2, 'Bite', -1, '1d6', 1, 'Melee Weapon Attack: +1 to hit, reach 5 ft., one target. Hit: 2 (1d6  1) slashing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7332, 'Giant Rat (Diseased)', 2, 'Bite', 2, '1d4', 4, 'Melee Weapon Attack: +4 to hit, reach 5 ft., one target. Hit: 3 (1d4 + 2) piercing damage. If the target is a creature, it must succeed on a DC 10 Constitution saving throw or contract a disease. Until the disease is cured, the target can''t regain hit points except by magical means, and the target''s hit point maximum decreases by 3 (1d6) every 24 hours. If the target''s hit point maximum drops to 0 as a result of this disease, the target dies.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7359, 'Giant Wolf Spider', 1, 'Spider Climb', 0, NULL, 0, 'The spider can climb difficult surfaces, including upside down on ceilings, without needing to make an ability check.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7360, 'Giant Wolf Spider', 1, 'Web Sense', 0, NULL, 0, 'While in contact with a web, the spider knows the exact location of any other creature in contact with the same web.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7361, 'Giant Wolf Spider', 1, 'Web Walker', 0, NULL, 0, 'The spider ignores movement restrictions caused by webbing.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7362, 'Giant Wolf Spider', 2, 'Bite', 1, '1d6', 3, 'Weapon Attack: +3 to hit, reach 5 ft., one creature. Hit: 4 (1d6 + 1) piercing damage, and the target must make a DC 11 Constitution saving throw, taking 7 (2d6) poison damage on a failed save, or half as much damage on a successful one. If the poison damage reduces the target to 0 hit points, the target is stable but poisoned for 1 hour, even after regaining hit points, and is paralyzed while poisoned in this way.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7396, 'Gray Ooze', 1, 'Amorphous', 0, NULL, 0, 'The ooze can move through a space as narrow as 1 inch wide without squeezing.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7397, 'Gray Ooze', 1, 'Corrode Metal', 0, NULL, 0, 'Any nonmagical weapon made of metal that hits the ooze corrodes. After dealing damage, the weapon takes a permanent and cumulative -1 penalty to damage rolls. If its penalty drops to -5, the weapon is destroyed. Nonmagical ammunition made of metal that hits the ooze is destroyed after dealing damage.
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7398, 'Gray Ooze', 1, 'False Appearance', 0, NULL, 0, 'While the ooze remains motionless, it is indistinguishable from an oily pool or wet rock.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7399, 'Gray Ooze', 2, 'Pseudopod', 1, '1d6', 3, 'Melee Weapon Attack: +3 to hit, reach 5 ft., one target. Hit: 4 (1d6 + 1) bludgeoning damage plus 7 (2d6) acid damage, and if the target is wearing nonmagical metal armor, its armor is partly corroded and takes a permanent and cumulative -1 penalty to the AC it offers. The armor is destroyed if the penalty reduces its AC to 10.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7427, 'Guardian Naga', 2, 'Bite', 4, '1d8', 8, 'Melee Weapon Attack: +8 to hit, reach 10 ft., one creature. Hit: 8 (1d8 + 4) piercing damage, and the target must make a DC 15 Constitution saving throw, taking 45 (10d8) poison damage on a failed save, or half as much damage on a successful one.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7428, 'Guardian Naga', 2, 'Spit Poison', 0, '10d8', 8, 'Ranged Weapon Attack: +8 to hit, range 15/30 ft., one creature. Hit: The target must make a DC 15 Constitution saving throw, taking 45 (10d8) poison damage on a failed save, or half as much damage on a successful one.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7471, 'Horned Devil', 1, 'Magic Resistance', 0, NULL, 0, 'The devil has advantage on saving throws against spells and other magical effects.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7484, 'Hydra', 2, 'Bite', 5, '1d10', 8, 'Melee Weapon Attack: +8 to hit, reach 10 ft., one target. Hit: 10 (1d10 + 5) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7485, 'Hyena', 1, 'Pack Tactics', 0, NULL, 0, 'The hyena has advantage on an attack roll against a creature if at least one of the hyena''s allies is within 5 ft. of the creature and the ally isn''t incapacitated.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7486, 'Hyena', 2, 'Bite', 0, '1d6', 2, 'Weapon Attack: +2 to hit, reach 5 ft., one target. Hit: 3 (1d6) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7488, 'Ice Devil', 1, 'Magic Resistance', 0, NULL, 0, 'The devil has advantage on saving throws against spells and other magical effects.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7489, 'Ice Devil', 2, 'Multiattack', 0, NULL, 0, 'The devil makes three attacks: one with its bite, one with its claws, and one with its tail.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7490, 'Ice Devil', 2, 'Bite', 5, '2d6 + 3d6', 10, 'Melee Weapon Attack: +10 to hit, reach 5 ft., one target. Hit: 12 (2d6 + 5) piercing damage plus 10 (3d6) cold damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7491, 'Ice Devil', 2, 'Claws', 5, '2d4 + 3d6', 10, 'Melee Weapon Attack: +10 to hit, reach 5 ft., one target. Hit: 10 (2d4 + 5) slashing damage plus 10 (3d6) cold damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7492, 'Ice Devil', 2, 'Tail', 5, '2d6 + 3d6', 10, 'Melee Weapon Attack:+10 to hit, reach 10 ft., one target. Hit: 12 (2d6 + 5) bludgeoning damage plus 10 (3d6) cold damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7493, 'Ice Devil', 2, 'Wall of Ice', 0, NULL, 0, 'The devil magically forms an opaque wall of ice on a solid surface it can see within 60 feet of it. The wall is 1 foot thick and up to 30 feet long and 10 feet high, or it''s a hemispherical dome up to 20 feet in diameter.
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7494, 'Ice Mephit', 1, 'Death Burst', 0, '1d8', 0, 'When the mephit dies, it explodes in a burst of jagged ice. Each creature within 5 ft. of it must make a DC 10 Dexterity saving throw, taking 4 (1d8) slashing damage on a failed save, or half as much damage on a successful one.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7495, 'Ice Mephit', 1, 'False Appearance', 0, NULL, 0, 'While the mephit remains motionless, it is indistinguishable from an ordinary shard of ice.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7496, 'Ice Mephit', 1, 'Innate Spellcasting (1/Day)', 0, NULL, 0, 'The mephit can innately cast fog cloud, requiring no material components. Its innate spellcasting ability is Charisma.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7497, 'Ice Mephit', 2, 'Claws', 1, '1d4', 3, 'Melee Weapon Attack: +3 to hit, reach 5 ft., one creature. Hit: 3 (1d4 + 1) slashing damage plus 2 (1d4) cold damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7498, 'Ice Mephit', 2, 'Frost Breath (Recharge 6)', 0, NULL, 0, 'The mephit exhales a 15-foot cone of cold air. Each creature in that area must succeed on a DC 10 Dexterity saving throw, taking 5 (2d4) cold damage on a failed save, or half as much damage on a successful one.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7499, 'Ice Mephit', 2, 'Variant: Summon Mephits (1/Day)', 0, NULL, 0, 'The mephit has a 25 percent chance of summoning 1d4 mephits of its kind. A summoned mephit appears in an unoccupied space within 60 feet of its summoner, acts as an ally of its summoner, and can''t summon other mephits. It remains for 1 minute, until it or its summoner dies, or until its summoner dismisses it as an action.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7500, 'Imp', 1, 'Shapechanger', 0, NULL, 0, 'The imp can use its action to polymorph into a beast form that resembles a rat (speed 20 ft.), a raven (20 ft., fly 60 ft.), or a spider (20 ft., climb 20 ft.), or back into its true form. Its statistics are the same in each form, except for the speed changes noted. Any equipment it is wearing or carrying isn''t transformed. It reverts to its true form if it dies.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7501, 'Imp', 1, 'Devil''s Sight', 0, NULL, 0, 'Magical darkness doesn''t impede the imp''s darkvision.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7502, 'Imp', 1, 'Magic Resistance', 0, NULL, 0, 'The imp has advantage on saving throws against spells and other magical effects.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7503, 'Imp', 1, 'Variant: Familiar', 0, NULL, 0, 'The imp can serve another creature as a familiar, forming a telepathic bond with its willing master. While the two are bonded, the master can sense what the quasit senses as long as they are within 1 mile of each other. While the imp is within 10 feet of its master, the master shares the quasit''s Magic Resistance trait. At any time and for any reason, the imp can end its resources as a familiar, ending the telepathic bond.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7504, 'Imp', 2, 'Sting (Bite in Beast Form)', 3, '1d4', 5, 'Melee Weapon Attack: +5 to hit, reach 5 ft ., one target. Hit: 5 (1d4 + 3) piercing damage, and the target must make on a DC 11 Constitution saving throw, taking 10 (3d6) poison damage on a failed save, or half as much damage on a successful one.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7505, 'Imp', 2, 'Invisibility', 0, NULL, 0, 'The imp magically turns invisible until it attacks, or until its concentration ends (as if concentrating on a spell). Any equipment the imp wears or carries is invisible with it.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7507, 'Invisible Stalker', 1, 'Faultless Tracker', 0, NULL, 0, 'The stalker is given a quarry by its summoner. The stalker knows the direction and distance to its quarry as long as the two of them are on the same plane of existence. The stalker also knows the location of its summoner.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7508, 'Invisible Stalker', 2, 'Multiattack', 0, NULL, 0, 'The stalker makes two slam attacks.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7509, 'Invisible Stalker', 2, 'Slam', 3, '2d6', 6, 'Melee Weapon Attack: +6 to hit, reach 5 ft., one target. Hit: 10 (2d6 + 3) bludgeoning damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7510, 'Iron Golem', 1, 'Fire Absorption', 0, NULL, 0, 'Whenever the golem is subjected to fire damage, it takes no damage and instead regains a number of hit points equal to the fire damage dealt.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7511, 'Iron Golem', 1, 'Immutable Form', 0, NULL, 0, 'The golem is immune to any spell or effect that would alter its form.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7512, 'Iron Golem', 1, 'Magic Resistance', 0, NULL, 0, 'The golem has advantage on saving throws against spells and other magical effects.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7513, 'Iron Golem', 1, 'Magic Weapons', 0, NULL, 0, 'The golem''s weapon attacks are magical.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7514, 'Iron Golem', 2, 'Multiattack', 0, NULL, 0, 'The golem makes two melee attacks.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7515, 'Iron Golem', 2, 'Slam', 7, '3d8', 13, 'Melee Weapon Attack: +13 to hit, reach 5 ft., one target. Hit: 20 (3d8 + 7) bludgeoning damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7516, 'Iron Golem', 2, 'Sword', 7, '3d10', 13, 'Melee Weapon Attack: +13 to hit, reach 10 ft., one target. Hit: 23 (3d10 + 7) slashing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7517, 'Iron Golem', 2, 'Poison Breath (Recharge 5-6)', 0, '10d8', 0, 'The golem exhales poisonous gas in a 15-foot cone. Each creature in that area must make a DC 19 Constitution saving throw, taking 45 (l0d8) poison damage on a failed save, or half as much damage on a successful one.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7518, 'Jackal', 1, 'Keen Hearing and Smell', 0, NULL, 0, 'The jackal has advantage on Wisdom (Perception) checks that rely on hearing or smell.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7519, 'Jackal', 1, 'Pack Tactics', 0, NULL, 0, 'The jackal has advantage on an attack roll against a creature if at least one of the jackal''s allies is within 5 ft. of the creature and the ally isn''t incapacitated.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7520, 'Jackal', 2, 'Bite', -1, '1d4', 1, 'Melee Weapon Attack: +1 to hit, reach 5 ft., one target. Hit: 1 (1d4  1) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7522, 'Killer Whale', 1, 'Hold Breath', 0, NULL, 0, 'The whale can hold its breath for 30 minutes');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7523, 'Killer Whale', 1, 'Keen Hearing', 0, NULL, 0, 'The whale has advantage on Wisdom (Perception) checks that rely on hearing.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7524, 'Killer Whale', 2, 'Bite', 0, NULL, 0, 'Melee Weapon Attack: +6 to hit, reach 5 ft., one target. Hit: 21 (5d6 + 4) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7525, 'Knight', 1, 'Brave', 0, NULL, 0, 'The knight has advantage on saving throws against being frightened.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7526, 'Knight', 2, 'Multiattack', 0, NULL, 0, 'The knight makes two melee attacks.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7527, 'Knight', 2, 'Greatsword', 3, '2d6', 5, 'Melee Weapon Attack: +5 to hit, reach 5 ft., one target. Hit: 10 (2d6 + 3) slashing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7528, 'Knight', 2, 'Heavy Crossbow', 0, '1d10', 2, 'Ranged Weapon Attack: +2 to hit, range 100/400 ft., one target. Hit: 5 (1d10) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7529, 'Knight', 2, 'Leadership (Recharges after a Short or Long Rest)', 0, NULL, 0, 'For 1 minute, the knight can utter a special command or warning whenever a nonhostile creature that it can see within 30 ft. of it makes an attack roll or a saving throw. The creature can add a d4 to its roll provided it can hear and understand the knight. A creature can benefit from only one Leadership die at a time. This effect ends if the knight is incapacitated.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7530, 'Kobold', 1, 'Sunlight Sensitivity', 0, NULL, 0, 'While in sunlight, the kobold has disadvantage on attack rolls, as well as on Wisdom (Perception) checks that rely on sight.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7531, 'Kobold', 1, 'Pack Tactics', 0, NULL, 0, 'The kobold has advantage on an attack roll against a creature if at least one of the kobold''s allies is within 5 ft. of the creature and the ally isn''t incapacitated.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7532, 'Kobold', 2, 'Dagger', 2, '1d4', 4, 'Melee Weapon Attack: +4 to hit, reach 5 ft., one target. Hit: 4 (1d4 + 2) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7533, 'Kobold', 2, 'Sling', 2, '1d4', 4, 'Ranged Weapon Attack: +4 to hit, range 30/120 ft., one target. Hit: 4 (1d4 + 2) bludgeoning damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7535, 'Kraken', 1, 'Freedom of Movement', 0, NULL, 0, 'The kraken ignores difficult terrain, and magical effects can''t reduce its speed or cause it to be restrained. It can spend 5 feet of movement to escape from nonmagical restraints or being grappled.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7536, 'Kraken', 1, 'Siege Monster', 0, NULL, 0, 'The kraken deals double damage to objects and structures.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7537, 'Kraken', 2, 'Multiattack', 0, NULL, 0, 'The kraken makes three tentacle attacks, each of which it can replace with one use of Fling.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7538, 'Kraken', 2, 'Bite', 10, '3d8', 7, 'Melee Weapon Attack: +7 to hit, reach 5 ft., one target. Hit: 23 (3d8 + 10) piercing damage. If the target is a Large or smaller creature grappled by the kraken, that creature is swallowed, and the grapple ends. While swallowed, the creature is blinded and restrained, it has total cover against attacks and other effects outside the kraken, and it takes 42 (12d6) acid damage at the start of each of the kraken''s turns. If the kraken takes 50 damage or more on a single turn from a creature inside it, the kraken must succeed on a DC 25 Constitution saving throw at the end of that turn or regurgitate all swallowed creatures, which fall prone in a space within 10 feet of the kraken. If the kraken dies, a swallowed creature is no longer restrained by it and can escape from the corpse using 15 feet of movement, exiting prone.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7539, 'Kraken', 2, 'Tentacle', 10, '3d6', 7, 'Melee Weapon Attack: +7 to hit, reach 30 ft., one target. Hit: 20 (3d6 + 10) bludgeoning damage, and the target is grappled (escape DC 18). Until this grapple ends, the target is restrained. The kraken has ten tentacles, each of which can grapple one target.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7540, 'Kraken', 2, 'Fling', 0, NULL, 0, 'One Large or smaller object held or creature grappled by the kraken is thrown up to 60 feet in a random direction and knocked prone. If a thrown target strikes a solid surface, the target takes 3 (1d6) bludgeoning damage for every 10 feet it was thrown. If the target is thrown at another creature, that creature must succeed on a DC 18 Dexterity saving throw or take the same damage and be knocked prone.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7541, 'Kraken', 2, 'Lightning Storm', 0, '4d10', 0, 'The kraken magically creates three bolts of lightning, each of which can strike a target the kraken can see within 120 feet of it. A target must make a DC 23 Dexterity saving throw, taking 22 (4d10) lightning damage on a failed save, or half as much damage on a successful one.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7542, 'Kraken', 3, 'Tentacle Attack or Fling', 0, NULL, 0, 'The kraken makes one tentacle attack or uses its Fling.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7543, 'Kraken', 3, 'Lightning Storm (Costs 2 Actions)', 0, NULL, 0, 'The kraken uses Lightning Storm.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7544, 'Kraken', 3, 'Ink Cloud (Costs 3 Actions)', 0, NULL, 0, 'While underwater, the kraken expels an ink cloud in a 60-foot radius. The cloud spreads around corners, and that area is heavily obscured to creatures other than the kraken. Each creature other than the kraken that ends its turn there must succeed on a DC 23 Constitution saving throw, taking 16 (3d10) poison damage on a failed save, or half as much damage on a successful one. A strong current disperses the cloud, which otherwise disappears at the end of the kraken''s next turn.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7545, 'Lamia', 1, 'Innate Spellcasting', 0, NULL, 0, 'The lamia''s innate spellcasting ability is Charisma (spell save DC 13). It can innately cast the following spells, requiring no material components. At will: disguise self (any humanoid form), major image 3/day each: charm person, mirror image, scrying, suggestion 1/day: geas');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7546, 'Lamia', 2, 'Multiattack', 0, NULL, 0, 'The lamia makes two attacks: one with its claws and one with its dagger or Intoxicating Touch.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7547, 'Lamia', 2, 'Claws', 3, '2d10', 5, 'Melee Weapon Attack: +5 to hit, reach 5 ft., one target. Hit: 14 (2d10 + 3) slashing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7548, 'Lamia', 2, 'Dagger', 3, '1d4', 5, 'Melee Weapon Attack: +5 to hit, reach 5 ft., one target. Hit: 5 (1d4 + 3) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7549, 'Lamia', 2, 'Intoxicating Touch', 0, NULL, 0, 'Melee Spell Attack: +5 to hit, reach 5 ft., one creature. Hit: The target is magically cursed for 1 hour. Until the curse ends, the target has disadvantage on Wisdom saving throws and all ability checks.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7550, 'Lemure', 1, 'Devil''s Sight', 0, NULL, 0, 'Magical darkness doesn''t impede the lemure''s darkvision.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7551, 'Lemure', 1, 'Hellish Rejuvenation', 0, NULL, 0, 'A lemure that dies in the Nine Hells comes back to life with all its hit points in 1d10 days unless it is killed by a good-aligned creature with a bless spell cast on that creature or its remains are sprinkled with holy water.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7552, 'Lemure', 2, 'Fist', 0, '1d4', 3, 'Melee Weapon Attack: +3 to hit, reach 5 ft., one target. Hit: 2 (1d4) bludgeoning damage');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7553, 'Lich', 1, 'Legendary Resistance (3/Day)', 0, NULL, 0, 'If the lich fails a saving throw, it can choose to succeed instead.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7554, 'Lich', 1, 'Rejuvenation', 0, NULL, 0, 'If it has a phylactery, a destroyed lich gains a new body in 1d10 days, regaining all its hit points and becoming active again. The new body appears within 5 feet of the phylactery.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7555, 'Lich', 1, 'Spellcasting', 0, NULL, 0, 'The lich is an 18th-level spellcaster. Its spellcasting ability is Intelligence (spell save DC 20, +12 to hit with spell attacks). The lich has the following wizard spells prepared:
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7556, 'Lich', 1, 'Turn Resistance', 0, NULL, 0, 'The lich has advantage on saving throws against any effect that turns undead.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7557, 'Lich', 2, 'Paralyzing Touch', 0, '3d6', 12, 'Melee Spell Attack: +12 to hit, reach 5 ft., one creature. Hit: 10 (3d6) cold damage. The target must succeed on a DC 18 Constitution saving throw or be paralyzed for 1 minute. The target can repeat the saving throw at the end of each of its turns, ending the effect on itself on a success.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7558, 'Lich', 3, 'Cantrip', 0, NULL, 0, 'The lich casts a cantrip.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7559, 'Lich', 3, 'Paralyzing Touch (Costs 2 Actions)', 0, NULL, 0, 'The lich uses its Paralyzing Touch.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7560, 'Lich', 3, 'Frightening Gaze (Costs 2 Actions)', 0, NULL, 0, 'The lich fixes its gaze on one creature it can see within 10 feet of it. The target must succeed on a DC 18 Wisdom saving throw against this magic or become frightened for 1 minute. The frightened target can repeat the saving throw at the end of each of its turns, ending the effect on itself on a success. If a target''s saving throw is successful or the effect ends for it, the target is immune to the lich''s gaze for the next 24 hours.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7561, 'Lich', 3, 'Disrupt Life (Costs 3 Actions)', 0, '6d6', 0, 'Each living creature within 20 feet of the lich must make a DC 18 Constitution saving throw against this magic, taking 21 (6d6) necrotic damage on a failed save, or half as much damage on a successful one.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7562, 'Lion', 1, 'Keen Smell', 0, NULL, 0, 'The lion has advantage on Wisdom (Perception) checks that rely on smell.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7563, 'Lion', 1, 'Pack Tactics', 0, NULL, 0, 'The lion has advantage on an attack roll against a creature if at least one of the lion''s allies is within 5 ft. of the creature and the ally isn''t incapacitated.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7564, 'Lion', 1, 'Pounce', 0, NULL, 0, 'If the lion moves at least 20 ft. straight toward a creature and then hits it with a claw attack on the same turn, that target must succeed on a DC 13 Strength saving throw or be knocked prone. If the target is prone, the lion can make one bite attack against it as a bonus action.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7565, 'Lion', 1, 'Running Leap', 0, NULL, 0, 'With a 10-foot running start, the lion can long jump up to 25 ft..');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7566, 'Lion', 2, 'Bite', 3, '1d8', 5, 'Melee Weapon Attack: +5 to hit, reach 5 ft., one target. Hit: 7 (1d8 + 3) piercing damage.');
