INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7570, 'Lizardfolk', 2, 'Multiattack', 0, NULL, 0, 'The lizardfolk makes two melee attacks, each one with a different weapon.');
