INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7594, 'Marilith', 1, 'Magic Resistance', 0, NULL, 0, 'The marilith has advantage on saving throws against spells and other magical effects.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7595, 'Marilith', 1, 'Magic Weapons', 0, NULL, 0, 'The marilith''s weapon attacks are magical.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7596, 'Marilith', 1, 'Reactive', 0, NULL, 0, 'The marilith can take one reaction on every turn in combat.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7597, 'Marilith', 2, 'Multiattack', 0, NULL, 0, 'The marilith can make seven attacks: six with its longswords and one with its tail.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7598, 'Marilith', 2, 'Longsword', 4, '2d8', 9, 'Melee Weapon Attack: +9 to hit, reach 5 ft., one target. Hit: 13 (2d8 + 4) slashing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7599, 'Marilith', 2, 'Tail', 4, '2d10', 9, 'Melee Weapon Attack: +9 to hit, reach 10 ft., one creature. Hit: 15 (2d10 + 4) bludgeoning damage. If the target is Medium or smaller, it is grappled (escape DC 19). Until this grapple ends, the target is restrained, the marilith can automatically hit the target with its tail, and the marilith can''t make tail attacks against other targets.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7600, 'Marilith', 2, 'Teleport', 0, NULL, 0, 'The marilith magically teleports, along with any equipment it is wearing or carrying, up to 120 feet to an unoccupied space it can see.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7610, 'Merfolk', 2, 'Spear', 0, '1d6', 2, 'Melee or Ranged Weapon Attack: +2 to hit, reach 5 ft. or range 20/60 ft., one target. Hit: 3 (1d6) piercing damage, or 4 (1d8) piercing damage if used with two hands to make a melee attack.');
