INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7602, 'Mastiff', 1, 'Keen Hearing and Smell', 0, NULL, 0, 'The mastiff has advantage on Wisdom (Perception) checks that rely on hearing or smell.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7603, 'Mastiff', 2, 'Bite', 1, '1d6', 3, 'Melee Weapon Attack: +3 to hit, reach 5 ft., one target. Hit: 4 (1d6 + 1) piercing damage. If the target is a creature, it must succeed on a DC 11 Strength saving throw or be knocked prone.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7604, 'Medusa', 1, 'Petrifying Gaze', 0, NULL, 0, 'When a creature that can see the medusa''s eyes starts its turn within 30 ft. of the medusa, the medusa can force it to make a DC 14 Constitution saving throw if the medusa isn''t incapacitated and can see the creature. If the saving throw fails by 5 or more, the creature is instantly petrified. Otherwise, a creature that fails the save begins to turn to stone and is restrained. The restrained creature must repeat the saving throw at the end of its next turn, becoming petrified on a failure or ending the effect on a success. The petrification lasts until the creature is freed by the greater restoration spell or other magic.
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7605, 'Medusa', 2, 'Multiattack', 0, NULL, 0, 'The medusa makes either three melee attacks   one with its snake hair and two with its shortsword  or two ranged attacks with its longbow.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7606, 'Medusa', 2, 'Snake Hair', 2, '1d4', 5, 'Melee Weapon Attack: +5 to hit, reach 5 ft., one creature. Hit: 4 (1d4 + 2) piercing damage plus 14 (4d6) poison damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7607, 'Medusa', 2, 'Shortsword', 2, '1d6', 5, 'Melee Weapon Attack: +5 to hit, reach 5 ft., one target. Hit: 5 (1d6 + 2) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7608, 'Medusa', 2, 'Longbow', 0, '2d6', 5, 'Ranged Weapon Attack: +5 to hit, range 150/600 ft., one target. Hit: 6 (1d8 + 2) piercing damage plus 7 (2d6) poison damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7612, 'Merrow', 2, 'Multiattack', 0, NULL, 0, 'The merrow makes two attacks: one with its bite and one with its claws or harpoon.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7613, 'Merrow', 2, 'Bite', 4, '1d8', 6, 'Melee Weapon Attack: +6 to hit, reach 5 ft., one target. Hit: 8 (1d8 + 4) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7614, 'Merrow', 2, 'Claws', 4, '2d4', 6, 'Melee Weapon Attack: +6 to hit, reach 5 ft., one target. Hit: 9 (2d4 + 4) slashing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7615, 'Merrow', 2, 'Harpoon', 4, '2d6', 6, 'Melee or Ranged Weapon Attack: +6 to hit, reach 5 ft. or range 20/60 ft., one target. Hit: 11 (2d6 + 4) piercing damage. If the target is a Huge or smaller creature, it must succeed on a Strength contest against the merrow or be pulled up to 20 feet toward the merrow.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7616, 'Mimic', 1, 'Shapechanger', 0, NULL, 0, 'The mimic can use its action to polymorph into an object or back into its true, amorphous form. Its statistics are the same in each form. Any equipment it is wearing or carrying isn ''t transformed. It reverts to its true form if it dies.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7617, 'Mimic', 1, 'Adhesive (Object Form Only)', 0, NULL, 0, 'The mimic adheres to anything that touches it. A Huge or smaller creature adhered to the mimic is also grappled by it (escape DC 13). Ability checks made to escape this grapple have disadvantage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7618, 'Mimic', 1, 'False Appearance (Object Form Only)', 0, NULL, 0, 'While the mimic remains motionless, it is indistinguishable from an ordinary object.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7619, 'Mimic', 1, 'Grappler', 0, NULL, 0, 'The mimic has advantage on attack rolls against any creature grappled by it.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7620, 'Mimic', 2, 'Pseudopod', 3, '1d8', 5, 'Melee Weapon Attack: +5 to hit, reach 5 ft., one target. Hit: 7 (1d8 + 3) bludgeoning damage. If the mimic is in object form, the target is subjected to its Adhesive trait.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7621, 'Mimic', 2, 'Bite', 3, '1d8 + 1d8', 5, 'Melee Weapon Attack: +5 to hit, reach 5 ft., one target. Hit: 7 (1d8 + 3) piercing damage plus 4 (1d8) acid damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7622, 'Minotaur', 1, 'Charge', 0, '2d8', 0, 'If the minotaur moves at least 10 ft. straight toward a target and then hits it with a gore attack on the same turn, the target takes an extra 9 (2d8) piercing damage. If the target is a creature, it must succeed on a DC 14 Strength saving throw or be pushed up to 10 ft. away and knocked prone.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7623, 'Minotaur', 1, 'Labyrinthine Recall', 0, NULL, 0, 'The minotaur can perfectly recall any path it has traveled.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7624, 'Minotaur', 1, 'Reckless', 0, NULL, 0, 'At the start of its turn, the minotaur can gain advantage on all melee weapon attack rolls it makes during that turn, but attack rolls against it have advantage until the start of its next turn.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7625, 'Minotaur', 2, 'Greataxe', 4, '2d12', 6, 'Melee Weapon Attack: +6 to hit, reach 5 ft., one target. Hit: 17 (2d12 + 4) slashing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7626, 'Minotaur', 2, 'Gore', 4, '2d8', 6, 'Melee Weapon Attack: +6 to hit, reach 5 ft., one target. Hit: 13 (2d8 + 4) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7627, 'Minotaur Skeleton', 1, 'Charge', 0, '2d8', 0, 'If the skeleton moves at least 10 feet straight toward a target and then hits it with a gore attack on the same turn, the target takes an extra 9 (2d8) piercing damage. If the target is a creature, it must succeed on a DC 14 Strength saving throw or be pushed up to 10 feet away and knocked prone.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7628, 'Minotaur Skeleton', 2, 'Greataxe', 4, '2d12', 6, 'Melee Weapon Attack: +6 to hit, reach 5 ft., one target. Hit: 17 (2d12 + 4) slashing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7629, 'Minotaur Skeleton', 2, 'Gore', 4, '2d8', 6, 'Melee Weapon Attack: +6 to hit, reach 5 ft., one target. Hit: 13 (2d8 + 4) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7630, 'Mule', 1, 'Beast of Burden', 0, NULL, 0, 'The mule is considered to be a Large animal for the purpose of determining its carrying capacity.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7631, 'Mule', 1, 'Sure-Footed', 0, NULL, 0, 'The mule has advantage on Strength and Dexterity saving throws made against effects that would knock it prone.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7632, 'Mule', 2, 'Hooves', 2, '1d4', 4, 'Melee Weapon Attack: +2 to hit, reach 5 ft., one target. Hit: 4 (1d4 + 2) bludgeoning damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7633, 'Mummy', 2, 'Multiattack', 0, NULL, 0, 'The mummy can use its Dreadful Glare and makes one attack with its rotting fist.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7634, 'Mummy', 2, 'Rotting Fist', 3, '2d6', 5, 'Melee Weapon Attack: +5 to hit, reach 5 ft., one target. Hit: 10 (2d6 + 3) bludgeoning damage plus 10 (3d6) necrotic damage. If the target is a creature, it must succeed on a DC 12 Constitution saving throw or be cursed with mummy rot. The cursed target can''t regain hit points, and its hit point maximum decreases by 10 (3d6) for every 24 hours that elapse. If the curse reduces the target''s hit point maximum to 0, the target dies, and its body turns to dust. The curse lasts until removed by the remove curse spell or other magic.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7635, 'Mummy', 2, 'Dreadful Glare', 0, NULL, 0, 'The mummy targets one creature it can see within 60 ft. of it. If the target can see the mummy, it must succeed on a DC 11 Wisdom saving throw against this magic or become frightened until the end of the mummy''s next turn. If the target fails the saving throw by 5 or more, it is also paralyzed for the same duration. A target that succeeds on the saving throw is immune to the Dreadful Glare of all mummies (but not mummy lords) for the next 24 hours.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7636, 'Mummy Lord', 1, 'Magic Resistance', 0, NULL, 0, 'The mummy lord has advantage on saving throws against spells and other magical effects.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7637, 'Mummy Lord', 1, 'Rejuvenation', 0, NULL, 0, 'A destroyed mummy lord gains a new body in 24 hours if its heart is intact, regaining all its hit points and becoming active again. The new body appears within 5 feet of the mummy lord''s heart.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7638, 'Mummy Lord', 1, 'Spellcasting', 0, NULL, 0, 'The mummy lord is a 10th-level spellcaster. Its spellcasting ability is Wisdom (spell save DC 17, +9 to hit with spell attacks). The mummy lord has the following cleric spells prepared:
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7639, 'Mummy Lord', 2, 'Multiattack', 0, NULL, 0, 'The mummy can use its Dreadful Glare and makes one attack with its rotting fist.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7640, 'Mummy Lord', 2, 'Rotting Fist', 4, '3d6 + 6d6', 9, 'Melee Weapon Attack: +9 to hit, reach 5 ft., one target. Hit: 14 (3d6 + 4) bludgeoning damage plus 21 (6d6) necrotic damage. If the target is a creature, it must succeed on a DC 16 Constitution saving throw or be cursed with mummy rot. The cursed target can''t regain hit points, and its hit point maximum decreases by 10 (3d6) for every 24 hours that elapse. If the curse reduces the target''s hit point maximum to 0, the target dies, and its body turns to dust. The curse lasts until removed by the remove curse spell or other magic.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7641, 'Mummy Lord', 2, 'Dreadful Glare', 0, NULL, 0, 'The mummy lord targets one creature it can see within 60 feet of it. If the target can see the mummy lord, it must succeed on a DC 16 Wisdom saving throw against this magic or become frightened until the end of the mummy''s next turn. If the target fails the saving throw by 5 or more, it is also paralyzed for the same duration. A target that succeeds on the saving throw is immune to the Dreadful Glare of all mummies and mummy lords for the next 24 hours.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7642, 'Mummy Lord', 3, 'Attack', 0, NULL, 0, 'The mummy lord makes one attack with its rotting fist or uses its Dreadful Glare.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7703, 'Panther', 1, 'Keen Smell', 0, NULL, 0, 'The panther has advantage on Wisdom (Perception) checks that rely on smell.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7643, 'Mummy Lord', 3, 'Blinding Dust', 0, NULL, 0, 'Blinding dust and sand swirls magically around the mummy lord. Each creature within 5 feet of the mummy lord must succeed on a DC 16 Constitution saving throw or be blinded until the end of the creature''s next turn.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7644, 'Mummy Lord', 3, 'Blasphemous Word (Costs 2 Actions)', 0, NULL, 0, 'The mummy lord utters a blasphemous word. Each non-undead creature within 10 feet of the mummy lord that can hear the magical utterance must succeed on a DC 16 Constitution saving throw or be stunned until the end of the mummy lord''s next turn.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7645, 'Mummy Lord', 3, 'Channel Negative Energy (Costs 2 Actions)', 0, NULL, 0, 'The mummy lord magically unleashes negative energy. Creatures within 60 feet of the mummy lord, including ones behind barriers and around corners, can''t regain hit points until the end of the mummy lord''s next turn.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7646, 'Mummy Lord', 3, 'Whirlwind of Sand (Costs 2 Actions)', 0, NULL, 0, 'The mummy lord magically transforms into a whirlwind of sand, moves up to 60 feet, and reverts to its normal form. While in whirlwind form, the mummy lord is immune to all damage, and it can''t be grappled, petrified, knocked prone, restrained, or stunned. Equipment worn or carried by the mummy lord remain in its possession.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7647, 'Nalfeshnee', 1, 'Magic Resistance', 0, NULL, 0, 'The nalfeshnee has advantage on saving throws against spells and other magical effects.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7648, 'Nalfeshnee', 2, 'Multiattack', 0, NULL, 0, 'The nalfeshnee uses Horror Nimbus if it can.  It then makes three attacks: one with its bite and two with its claws.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7649, 'Nalfeshnee', 2, 'Bite', 5, '5d10', 10, 'Melee Weapon Attack: +10 to hit, reach 5 ft., one target. Hit: 32 (5d10 + 5) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7650, 'Nalfeshnee', 2, 'Claw', 5, '3d6', 10, 'Melee Weapon Attack: +10 to hit, reach 10 ft., one target. Hit: 15 (3d6 + 5) slashing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7651, 'Nalfeshnee', 2, 'Horror Nimbus (Recharge 5-6)', 0, NULL, 0, 'The nalfeshnee magically emits scintillating, multicolored light. Each creature within 15 feet of the nalfeshnee that can see the light must succeed on a DC 15 Wisdom saving throw or be frightened for 1 minute. A creature can repeat the saving throw at the end of each of its turns, ending the effect on itself on a success. If a creature''s saving throw is successful or the effect ends for it, the creature is immune to the nalfeshnee''s Horror Nimbus for the next 24 hours.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7652, 'Nalfeshnee', 2, 'Teleport', 0, NULL, 0, 'The nalfeshnee magically teleports, along with any equipment it is wearing or carrying, up to 120 feet to an unoccupied space it can see.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7653, 'Nalfeshnee', 2, 'Variant: Summon Demon (1/Day)', 0, NULL, 0, 'The demon chooses what to summon and attempts a magical summoning.
