INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7712, 'Pit Fiend', 1, 'Fear Aura', 0, NULL, 0, 'Any creature hostile to the pit fiend that starts its turn within 20 feet of the pit fiend must make a DC 21 Wisdom saving throw, unless the pit fiend is incapacitated. On a failed save, the creature is frightened until the start of its next turn. If a creature''s saving throw is successful, the creature is immune to the pit fiend''s Fear Aura for the next 24 hours.');
