INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7741, 'Pseudodragon', 1, 'Limited Telepathy', 0, NULL, 0, 'The pseudodragon can magically communicate simple ideas, emotions, and images telepathically with any creature within 100 ft. of it that can understand a language.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7742, 'Pseudodragon', 1, 'Variant: Familiar', 0, NULL, 0, 'The pseudodragon can serve another creature as a familiar, forming a magic, telepathic bond with that willing companion. While the two are bonded, the companion can sense what the pseudodragon senses as long as they are within 1 mile of each other. While the pseudodragon is within 10 feet of its companion, the companion shares the pseudodragon''s Magic Resistance trait. At any time and for any reason, the pseudodragon can end its resources as a familiar, ending the telepathic bond.');
