INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7744, 'Pseudodragon', 2, 'Sting', 2, '1d4', 4, 'Melee Weapon Attack: +4 to hit, reach 5 ft., one creature. Hit: 4 (1d4 + 2) piercing damage, and the target must succeed on a DC 11 Constitution saving throw or become poisoned for 1 hour. If the saving throw fails by 5 or more, the target falls unconscious for the same duration, or until it takes damage or another creature uses an action to shake it awake.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7745, 'Purple Worm', 1, 'Tunneler', 0, NULL, 0, 'The worm can burrow through solid rock at half its burrow speed and leaves a 10-foot-diameter tunnel in its wake.');
