INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7765, 'Raven', 2, 'Beak', 0, NULL, 0, 'Melee Weapon Attack: +4 to hit, reach 5 ft., one target. Hit: 1 piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7766, 'Red Dragon Wyrmling', 2, 'Bite', 4, '1d10 + 1d6', 6, 'Melee Weapon Attack: +6 to hit, reach 5 ft., one target. Hit: 9 (1d10 + 4) piercing damage plus 3 (1d6) fire damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7767, 'Red Dragon Wyrmling', 2, 'Fire Breath (Recharge 5-6)', 0, '7d6', 0, 'The dragon exhales fire in a 15-foot cone. Each creature in that area must make a DC l3 Dexterity saving throw, taking 24 (7d6) fire damage on a failed save, or half as much damage on a successful one.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7800, 'Sahuagin', 1, 'Blood Frenzy', 0, NULL, 0, 'The sahuagin has advantage on melee attack rolls against any creature that doesn''t have all its hit points.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7768, 'Reef Shark', 1, 'Pack Tactics', 0, NULL, 0, 'The shark has advantage on an attack roll against a creature if at least one of the shark''s allies is within 5 ft. of the creature and the ally isn''t incapacitated.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7769, 'Reef Shark', 1, 'Water Breathing', 0, NULL, 0, 'The shark can breathe only underwater.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7770, 'Reef Shark', 2, 'Bite', 2, '1d8', 4, 'Melee Weapon Attack: +4 to hit, reach 5 ft., one target. Hit: 6 (1d8 + 2) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7771, 'Remorhaz', 1, 'Heated Body', 0, '3d6', 0, 'A creature that touches the remorhaz or hits it with a melee attack while within 5 feet of it takes 10 (3d6) fire damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7772, 'Remorhaz', 2, 'Bite', 7, '6d10 + 3d6', 11, 'Melee Weapon Attack: +11 to hit, reach 10 ft., one target. Hit: 40 (6d10 + 7) piercing damage plus 10 (3d6) fire damage. If the target is a creature, it is grappled (escape DC 17). Until this grapple ends, the target is restrained, and the remorhaz can''t bite another target.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7773, 'Remorhaz', 2, 'Swallow', 0, NULL, 0, 'The remorhaz makes one bite attack against a Medium or smaller creature it is grappling. If the attack hits, that creature takes the bite''s damage and is swallowed, and the grapple ends. While swallowed, the creature is blinded and restrained, it has total cover against attacks and other effects outside the remorhaz, and it takes 21 (6d6) acid damage at the start of each of the remorhaz''s turns.
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7774, 'Rhinoceros', 1, 'Charge', 0, '2d8', 0, 'If the rhinoceros moves at least 20 ft. straight toward a target and then hits it with a gore attack on the same turn, the target takes an extra 9 (2d8) bludgeoning damage. If the target is a creature, it must succeed on a DC 15 Strength saving throw or be knocked prone.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7775, 'Rhinoceros', 2, 'Gore', 5, '2d8', 7, 'Melee Weapon Attack: +7 to hit, reach 5 ft., one target. Hit: 14 (2d8 + 5) bludgeoning damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7776, 'Riding Horse', 2, 'Hooves', 3, '2d4', 5, 'Melee Weapon Attack: +5 to hit, reach 5 ft., one target. Hit: 8 (2d4 + 3) bludgeoning damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7777, 'Roc', 1, 'Keen Sight', 0, NULL, 0, 'The roc has advantage on Wisdom (Perception) checks that rely on sight.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7778, 'Roc', 2, 'Multiattack', 0, NULL, 0, 'The roc makes two attacks: one with its beak and one with its talons.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7779, 'Roc', 2, 'Beak', 9, '4d8', 13, 'Melee Weapon Attack: +13 to hit, reach 10 ft., one target. Hit: 27 (4d8 + 9) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7780, 'Roc', 2, 'Talons', 9, '4d6', 13, 'Melee Weapon Attack: +13 to hit, reach 5 ft., one target. Hit: 23 (4d6 + 9) slashing damage, and the target is grappled (escape DC 19). Until this grapple ends, the target is restrained, and the roc can''t use its talons on another target.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7781, 'Roper', 1, 'False Appearance', 0, NULL, 0, 'While the roper remains motionless, it is indistinguishable from a normal cave formation, such as a stalagmite.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7782, 'Roper', 1, 'Grasping Tendrils', 0, NULL, 0, 'The roper can have up to six tendrils at a time. Each tendril can be attacked (AC 20; 10 hit points; immunity to poison and psychic damage). Destroying a tendril deals no damage to the roper, which can extrude a replacement tendril on its next turn. A tendril can also be broken if a creature takes an action and succeeds on a DC 15 Strength check against it.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7783, 'Roper', 1, 'Spider Climb', 0, NULL, 0, 'The roper can climb difficult surfaces, including upside down on ceilings, without needing to make an ability check.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7784, 'Roper', 2, 'Multiattack', 0, NULL, 0, 'The roper makes four attacks with its tendrils, uses Reel, and makes one attack with its bite.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7785, 'Roper', 2, 'Bite', 4, '4d8', 7, 'Melee Weapon Attack: +7 to hit, reach 5 ft., one target. Hit: 22 (4d8 + 4) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7786, 'Roper', 2, 'Tendril', 0, NULL, 7, 'Melee Weapon Attack: +7 to hit, reach 50 ft., one creature. Hit: The target is grappled (escape DC 15). Until the grapple ends, the target is restrained and has disadvantage on Strength checks and Strength saving throws, and the roper can''t use the same tendril on another target.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7787, 'Roper', 2, 'Reel', 0, NULL, 0, 'The roper pulls each creature grappled by it up to 25 ft. straight toward it.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7788, 'Rug of Smothering', 1, 'Antimagic Susceptibility', 0, NULL, 0, 'The rug is incapacitated while in the area of an antimagic field. If targeted by dispel magic, the rug must succeed on a Constitution saving throw against the caster''s spell save DC or fall unconscious for 1 minute.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7789, 'Rug of Smothering', 1, 'Damage Transfer', 0, NULL, 0, 'While it is grappling a creature, the rug takes only half the damage dealt to it, and the creature grappled by the rug takes the other half.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7790, 'Rug of Smothering', 1, 'False Appearance', 0, NULL, 0, 'While the rug remains motionless, it is indistinguishable from a normal rug.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7791, 'Rug of Smothering', 2, 'Smother', 3, '2d6', 0, 'Melee Weapon Attack: +5 to hit, reach 5 ft., one Medium or smaller creature. Hit: The creature is grappled (escape DC 13). Until this grapple ends, the target is restrained, blinded, and at risk of suffocating, and the rug can''t smother another target. In addition, at the start of each of the target''s turns, the target takes 10 (2d6 + 3) bludgeoning damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7792, 'Rust Monster', 1, 'Iron Scent', 0, NULL, 0, 'The rust monster can pinpoint, by scent, the location of ferrous metal within 30 feet of it.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7793, 'Rust Monster', 1, 'Rust Metal', 0, NULL, 0, 'Any nonmagical weapon made of metal that hits the rust monster corrodes. After dealing damage, the weapon takes a permanent and cumulative -1 penalty to damage rolls. If its penalty drops to -5, the weapon is destroyed. Non magical ammunition made of metal that hits the rust monster is destroyed after dealing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7794, 'Rust Monster', 2, 'Bite', 1, '1d8', 3, 'Melee Weapon Attack: +3 to hit, reach 5 ft., one target. Hit: 5 (1d8 + 1) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7795, 'Rust Monster', 2, 'Antennae', 0, NULL, 0, 'The rust monster corrodes a nonmagical ferrous metal object it can see within 5 feet of it. If the object isn''t being worn or carried, the touch destroys a 1-foot cube of it. If the object is being worn or carried by a creature, the creature can make a DC 11 Dexterity saving throw to avoid the rust monster''s touch.
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7796, 'Saber-Toothed Tiger', 1, 'Keen Smell', 0, NULL, 0, 'The tiger has advantage on Wisdom (Perception) checks that rely on smell.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7797, 'Saber-Toothed Tiger', 1, 'Pounce', 0, NULL, 0, 'If the tiger moves at least 20 ft. straight toward a creature and then hits it with a claw attack on the same turn, that target must succeed on a DC 14 Strength saving throw or be knocked prone. If the target is prone, the tiger can make one bite attack against it as a bonus action.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7798, 'Saber-Toothed Tiger', 2, 'Bite', 5, '1d10', 6, 'Melee Weapon Attack: +6 to hit, reach 5 ft., one target. Hit: 10 (1d10 + 5) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7799, 'Saber-Toothed Tiger', 2, 'Claw', 5, '2d6', 6, 'Melee Weapon Attack: +6 to hit, reach 5 ft., one target. Hit: 12 (2d6 + 5) slashing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7801, 'Sahuagin', 1, 'Limited Amphibiousness', 0, NULL, 0, 'The sahuagin can breathe air and water, but it needs to be submerged at least once every 4 hours to avoid suffocating.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7802, 'Sahuagin', 1, 'Shark Telepathy', 0, NULL, 0, 'The sahuagin can magically command any shark within 120 feet of it, using a limited telepathy.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7803, 'Sahuagin', 2, 'Multiattack', 0, NULL, 0, 'The sahuagin makes two melee attacks: one with its bite and one with its claws or spear.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7804, 'Sahuagin', 2, 'Bite', 1, '1d4', 3, 'Melee Weapon Attack: +3 to hit, reach 5 ft., one target. Hit: 3 (1d4 + 1) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7805, 'Sahuagin', 2, 'Claws', 1, '1d4', 3, 'Melee Weapon Attack: +3 to hit, reach 5 ft., one target. Hit: 3 (1d4 + 1) slashing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7806, 'Sahuagin', 2, 'Spear', 1, '1d6', 3, 'Melee or Ranged Weapon Attack: +3 to hit, reach 5 ft. or range 20/60 ft., one target. Hit: 4 (1d6 + 1) piercing damage, or 5 (1d8 + 1) piercing damage if used with two hands to make a melee attack.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7807, 'Salamander', 1, 'Heated Body', 0, '2d6', 0, 'A creature that touches the salamander or hits it with a melee attack while within 5 ft. of it takes 7 (2d6) fire damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7808, 'Salamander', 1, 'Heated Weapons', 0, NULL, 0, 'Any metal melee weapon the salamander wields deals an extra 3 (1d6) fire damage on a hit (included in the attack).');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7809, 'Salamander', 2, 'Multiattack', 0, NULL, 0, 'The salamander makes two attacks: one with its spear and one with its tail.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7810, 'Salamander', 2, 'Spear', 4, '2d6', 7, 'Melee or Ranged Weapon Attack: +7 to hit, reach 5 ft. or range 20 ft./60 ft., one target. Hit: 11 (2d6 + 4) piercing damage, or 13 (2d8 + 4) piercing damage if used with two hands to make a melee attack, plus 3 (1d6) fire damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7811, 'Salamander', 2, 'Tail', 4, '2d6 + 2d6', 7, 'Melee Weapon Attack: +7 to hit, reach 10 ft., one target. Hit: 11 (2d6 + 4) bludgeoning damage plus 7 (2d6) fire damage, and the target is grappled (escape DC 14). Until this grapple ends, the target is restrained, the salamander can automatically hit the target with its tail, and the salamander can''t make tail attacks against other targets.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7812, 'Satyr', 1, 'Magic Resistance', 0, NULL, 0, 'The satyr has advantage on saving throws against spells and other magical effects.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7813, 'Satyr', 2, 'Ram', 1, '2d4', 3, 'Melee Weapon Attack: +3 to hit, reach 5 ft., one target. Hit: 6 (2d4 + 1) bludgeoning damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7814, 'Satyr', 2, 'Shortsword', 3, '1d6', 5, 'Melee Weapon Attack: +5 to hit, reach 5 ft., one target. Hit: 6 (1 d6 + 3) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7815, 'Satyr', 2, 'Shortbow', 3, '1d6', 5, 'Ranged Weapon Attack: +5 to hit, range 80/320 ft., one target. Hit: 6 (1d6 + 3) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7816, 'Satyr', 2, 'Variant: Panpipes', 0, NULL, 0, 'Gentle Lullaby. The creature falls asleep and is unconscious for 1 minute. The effect ends if the creature takes damage or if someone takes an action to shake the creature awake.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7817, 'Scorpion', 2, 'Sting', 1, NULL, 2, 'Melee Weapon Attack: +2 to hit, reach 5 ft., one creature. Hit: 1 piercing damage, and the target must make a DC 9 Constitution saving throw, taking 4 (1d8) poison damage on a failed save, or half as much damage on a successful one.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7818, 'Scout', 1, 'Keen Hearing and Sight', 0, NULL, 0, 'The scout has advantage on Wisdom (Perception) checks that rely on hearing or sight.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7819, 'Scout', 2, 'Multiattack', 0, NULL, 0, 'The scout makes two melee attacks or two ranged attacks.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7820, 'Scout', 2, 'Shortsword', 2, '1d6', 4, 'Melee Weapon Attack: +4 to hit, reach 5 ft., one target. Hit: 5 (1d6 + 2) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7821, 'Scout', 2, 'Longbow', 2, '1d8', 4, 'Ranged Weapon Attack: +4 to hit, ranged 150/600 ft., one target. Hit: 6 (1d8 + 2) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7823, 'Sea Hag', 1, 'Horrific Appearance', 0, NULL, 0, 'Any humanoid that starts its turn within 30 feet of the hag and can see the hag''s true form must make a DC 11 Wisdom saving throw. On a failed save, the creature is frightened for 1 minute. A creature can repeat the saving throw at the end of each of its turns, with disadvantage if the hag is within line of sight, ending the effect on itself on a success. If a creature''s saving throw is successful or the effect ends for it, the creature is immune to the hag''s Horrific Appearance for the next 24 hours.
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7824, 'Sea Hag', 1, 'Hag Coven', 0, NULL, 0, 'When hags must work together, they form covens, in spite of their selfish natures. A coven is made up of hags of any type, all of whom are equals within the group. However, each of the hags continues to desire more personal power.
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7825, 'Sea Hag', 1, 'Shared Spellcasting (Coven Only)', 0, NULL, 0, 'While all three members of a hag coven are within 30 feet of one another, they can each cast the following spells from the wizard''s spell list but must share the spell slots among themselves:
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7826, 'Sea Hag', 1, 'Hag Eye (Coven Only)', 0, NULL, 0, 'A hag coven can craft a magic item called a hag eye, which is made from a real eye coated in varnish and often fitted to a pendant or other wearable item. The hag eye is usually entrusted to a minion for safekeeping and transport. A hag in the coven can take an action to see what the hag eye sees if the hag eye is on the same plane of existence. A hag eye has AC 10, 1 hit point, and darkvision with a radius of 60 feet. If it is destroyed, each coven member takes 3d10 psychic damage and is blinded for 24 hours.
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7827, 'Sea Hag', 2, 'Claws', 3, '2d6', 5, 'Melee Weapon Attack: +5 to hit, reach 5 ft., one target. Hit: 10 (2d6 + 3) slashing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7828, 'Sea Hag', 2, 'Death Glare', 0, NULL, 0, 'The hag targets one frightened creature she can see within 30 ft. of her. If the target can see the hag, it must succeed on a DC 11 Wisdom saving throw against this magic or drop to 0 hit points.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7829, 'Sea Hag', 2, 'Illusory Appearance', 0, NULL, 0, 'The hag covers herself and anything she is wearing or carrying with a magical illusion that makes her look like an ugly creature of her general size and humanoid shape. The effect ends if the hag takes a bonus action to end it or if she dies.
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7830, 'Sea Horse', 1, 'Water Breathing', 0, NULL, 0, 'The sea horse can breathe only underwater.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7831, 'Shadow', 1, 'Amorphous', 0, NULL, 0, 'The shadow can move through a space as narrow as 1 inch wide without squeezing.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7832, 'Shadow', 1, 'Shadow Stealth', 0, NULL, 0, 'While in dim light or darkness, the shadow can take the Hide action as a bonus action. Its stealth bonus is also improved to +6.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7833, 'Shadow', 1, 'Sunlight Weakness', 0, NULL, 0, 'While in sunlight, the shadow has disadvantage on attack rolls, ability checks, and saving throws.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7834, 'Shadow', 2, 'Strength Drain', 2, '2d6', 4, 'Melee Weapon Attack: +4 to hit, reach 5 ft., one creature. Hit: 9 (2d6 + 2) necrotic damage, and the target''s Strength score is reduced by 1d4. The target dies if this reduces its Strength to 0. Otherwise, the reduction lasts until the target finishes a short or long rest.
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7835, 'Shambling Mound', 1, 'Lightning Absorption', 0, NULL, 0, 'Whenever the shambling mound is subjected to lightning damage, it takes no damage and regains a number of hit points equal to the lightning damage dealt.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7836, 'Shambling Mound', 2, 'Multiattack', 0, NULL, 0, 'The shambling mound makes two slam attacks. If both attacks hit a Medium or smaller target, the target is grappled (escape DC 14), and the shambling mound uses its Engulf on it.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7837, 'Shambling Mound', 2, 'Slam', 4, '2d8', 7, 'Melee Weapon Attack: +7 to hit, reach 5 ft., one target. Hit: 13 (2d8 + 4) bludgeoning damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7838, 'Shambling Mound', 2, 'Engulf', 0, NULL, 0, 'The shambling mound engulfs a Medium or smaller creature grappled by it. The engulfed target is blinded, restrained, and unable to breathe, and it must succeed on a DC 14 Constitution saving throw at the start of each of the mound''s turns or take 13 (2d8 + 4) bludgeoning damage. If the mound moves, the engulfed target moves with it. The mound can have only one creature engulfed at a time.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7839, 'Shield Guardian', 1, 'Bound', 0, NULL, 0, 'The shield guardian is magically bound to an amulet. As long as the guardian and its amulet are on the same plane of existence, the amulet''s wearer can telepathically call the guardian to travel to it, and the guardian knows the distance and direction to the amulet. If the guardian is within 60 feet of the amulet''s wearer, half of any damage the wearer takes (rounded up) is transferred to the guardian.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7840, 'Shield Guardian', 1, 'Regeneration', 0, NULL, 0, 'The shield guardian regains 10 hit points at the start of its turn if it has at least 1 hit. point.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7841, 'Shield Guardian', 1, 'Spell Storing', 0, NULL, 0, 'A spellcaster who wears the shield guardian''s amulet can cause the guardian to store one spell of 4th level or lower. To do so, the wearer must cast the spell on the guardian. The spell has no effect but is stored within the guardian. When commanded to do so by the wearer or when a situation arises that was predefined by the spellcaster, the guardian casts the stored spell with any parameters set by the original caster, requiring no components. When the spell is cast or a new spell is stored, any previously stored spell is lost.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7842, 'Shield Guardian', 2, 'Multiattack', 0, NULL, 0, 'The guardian makes two fist attacks.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7843, 'Shield Guardian', 2, 'Fist', 4, '2d6', 7, 'Melee Weapon Attack: +7 to hit, reach 5 ft., one target. Hit: 11 (2d6 + 4) bludgeoning damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7844, 'Shrieker', 1, 'False Appearance', 0, NULL, 0, 'While the shrieker remains motionless, it is indistinguishable from an ordinary fungus.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7845, 'Shrieker', 2, 'Shriek', 0, NULL, 0, 'When bright light or a creature is within 30 feet of the shrieker, it emits a shriek audible within 300 feet of it. The shrieker continues to shriek until the disturbance moves out of range and for 1d4 of the shrieker''s turns afterward');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7846, 'Silver Dragon Wyrmling', 2, 'Bite', 4, '1d10', 6, 'Melee Weapon Attack: +6 to hit, reach 5 ft., one target. Hit: 9 (1d10 + 4) piercing damage.');
