INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7880, 'Spy', 2, 'Hand Crossbow', 2, '1d6', 4, 'Ranged Weapon Attack: +4 to hit, range 30/120 ft., one target. Hit: 5 (1d6 + 2) piercing damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7881, 'Steam Mephit', 1, 'Death Burst', 0, '1d8', 0, 'When the mephit dies, it explodes in a cloud of steam. Each creature within 5 ft. of the mephit must succeed on a DC 10 Dexterity saving throw or take 4 (1d8) fire damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7882, 'Steam Mephit', 1, 'Innate Spellcasting (1/Day)', 0, NULL, 0, 'The mephit can innately cast blur, requiring no material components. Its innate spellcasting ability is Charisma.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7883, 'Steam Mephit', 2, 'Claws', 0, '2d4', 2, 'Melee Weapon Attack: +2 to hit, reach 5 ft., one creature. Hit: 2 (1d4) slashing damage plus 2 (1d4) fire damage.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7884, 'Steam Mephit', 2, 'Steam Breath (Recharge 6)', 0, NULL, 0, 'The mephit exhales a 15-foot cone of scalding steam. Each creature in that area must succeed on a DC 10 Dexterity saving throw, taking 4 (1d8) fire damage on a failed save, or half as much damage on a successful one.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7885, 'Steam Mephit', 2, 'Variant: Summon Mephits (1/Day)', 0, NULL, 0, 'The mephit has a 25 percent chance of summoning 1d4 mephits of its kind. A summoned mephit appears in an unoccupied space within 60 feet of its summoner, acts as an ally of its summoner, and can''t summon other mephits. It remains for 1 minute, until it or its summoner dies, or until its summoner dismisses it as an action.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7886, 'Stirge', 2, 'Blood Drain', 3, '1d4', 5, 'Melee Weapon Attack: +5 to hit, reach 5 ft., one creature. Hit: 5 (1d4 + 3) piercing damage, and the stirge attaches to the target. While attached, the stirge doesn''t attack. Instead, at the start of each of the stirge''s turns, the target loses 5 (1d4 + 3) hit points due to blood loss.
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7887, 'Stone Giant', 1, 'Stone Camouflage', 0, NULL, 0, 'The giant has advantage on Dexterity (Stealth) checks made to hide in rocky terrain.');
