INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7891, 'Stone Golem', 1, 'Immutable Form', 0, NULL, 0, 'The golem is immune to any spell or effect that would alter its form.');
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7892, 'Stone Golem', 1, 'Magic Resistance', 0, NULL, 0, 'The golem has advantage on saving throws against spells and other magical effects.');
