INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7993, 'Vampire', 1, 'Misty Escape', 0, NULL, 0, 'When it drops to 0 hit points outside its resting place, the vampire transforms into a cloud of mist (as in the Shapechanger trait) instead of falling unconscious, provided that it isn''t in sunlight or running water. If it can''t transform, it is destroyed.
INSERT INTO action (action_id, monster_name, action_type, name, damage_bonus, damage_dice, attack_bonus, description) VALUES (7994, 'Vampire', 1, 'Regeneration', 0, NULL, 0, 'The vampire regains 20 hit points at the start of its turn if it has at least 1 hit point and isn''t in sunlight or running water. If the vampire takes radiant damage or damage from holy water, this trait doesn''t function at the start of the vampire''s next turn.');
